void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float yDisp = sin(iTime) * 0.1;
    float periodDisp = sin(iTime * 0.3352);

    float col = smoothstep(-0.1, 0.4 + yDisp, 1.0 - uv.y + sin(2.0 * 3.14 * uv.x * periodDisp + iTime) * 0.2);

    fragColor = vec4(col, col, col, 1.0);
}
