#define PI 3.14


float rand(float x){
    return fract(sin(x)*12000.);
}

float noise(float x){
    float i = floor(x);
    float f = fract(x);
    return mix(rand(i), rand(i+1.), smoothstep(0.,1.,f));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = fragCoord.xy/iResolution.yy;
    uv = vec2(uv.x - (iResolution.x - iResolution.y)/iResolution.x, uv.y);



    vec3 color = vec3(0.);
    uv -= 0.5;

    float teta = atan(uv.y, uv.x) * 4. ; 
    float dMax = mix(0.2, 0.5, noise(noise(teta ) + iTime * 0.5));


    float d = 1.-step(dMax, length(uv));
    color = vec3(d) * vec3(noise(iTime*0.1 ), noise(iTime*0.07 + 100.), noise(iTime*0.03)) * (1.-length(uv));

    fragColor = vec4(color,1.0);
}
