void init(vec2 u, inout vec4 o)
{
    vec2 iu = floor((u-.0*Res));
    if (
        (iu.y == 1. && (iu.x == 11. || iu.x == 14.) )
    )
        o.x = 4.;
    if(
        (iu.x == 1. && (iu.y == 3. || iu.y == 6. || iu.y == 9. || iu.y == 12. || iu.y == 15.))
       ||
       (iu.x == 15. && (iu.y == 3. || iu.y == 6.))
        ||
        (iu.y == 1. && (iu.x == 2. || iu.x == 5. || iu.x == 8.) )
        ||
        (iu.y == 15. && (iu.x == 4. || iu.x == 7. || iu.x == 10.) )
      )
        o.x = 7.;
    if(
        (iu.y == 1. && iu.x == 15.)
        ||
        (iu.y == 4. && iu.x == 15.)
        ||
        (iu.y >= 7. && iu.y < 16. && iu.x == 15.)
        ||
        (iu.x >= 12. && iu.x < 15. && iu.y == 15.)
        ||
        (iu.y == 15. && (iu.x == 3. || iu.x == 6. || iu.x == 9.) )
        ||
        (iu.x == 1. && (iu.y == 2. || iu.y == 5. || iu.y == 8. || iu.y == 11. || iu.y == 14.) )
        ||
        (iu.y == 1. && (iu.x == 3. || iu.x == 6. || iu.x == 9. || iu.x == 12. || iu.x == 15.) )
        )
        o.x = 1.;
    if (iu.x == 15. && iu.y == 0.)
        o.x = 5.;
    if (
        (iu.y == 0. && iu.x > 0. && iu.x < 15.)
       ||
        (iu.y == 16. && iu.x > 0. && iu.x < 16.)
        ||
        (iu.x == 0. && iu.y >.0 && iu.y < 16.)
        ||
        (iu.x == 16. && iu.y >.0 && iu.y < 16.)
        ||
        (iu.x == 14. && iu.y >1.0 && iu.y < 15.)
        ||
        (iu.x == 2. && iu.y >1.0 && iu.y < 15.)
        ||
        (iu.y == 2. && iu.x >1.0 && iu.x < 15.)
        ||
        (iu.y == 14. && iu.x >1.0 && iu.x < 15.)
       )
        o.x = 2.;

}

void mainImage(out vec4 o, in vec2 u)
{
    o -= o;
    if (iFrame == 0)
    {
	    init(u, o);
        init(u-20., o);
       	return;
    }
    o = t0(u/Res);
    float n,e,s,w;
    n = t0((u+vec2(+0.0, +1.0))/iResolution.xy).x;
    e = t0((u+vec2(+1.0, +0.0))/iResolution.xy).x;
    s = t0((u+vec2(+0.0, -1.0))/iResolution.xy).x;
    w = t0((u+vec2(-1.0, +0.0))/iResolution.xy).x;

    ivec4 neigh = ivec4(n,e,s,w);

    o.x = float(update(int(ceil(o.x)), neigh));
}
