void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // import image
    vec4 tex = texture(iChannel0,uv);

    // bloom
    float curve = 20.0;
    float luma = (max(max(tex.r,tex.g),tex.b)+min(min(tex.r,tex.g),tex.b))/2.0;
    float luma2 = pow(luma, sqrt(17.25866 + curve));

    // Output to screen
    fragColor = ((tex/luma * luma2)+tex);
}
