vec3 pcg3d(vec3 p){
    uvec3 q = floatBitsToUint(p)*1234567890u+123456789u;
    q.x +=q.y*q.z; q.y +=q.x*q.z; q.z+=q.x*q.y;
    q^=q>>16u;
    q.x +=q.y*q.z; q.y +=q.x*q.z; q.z+=q.x*q.y;
    return vec3(q)/float(-1U);
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy -.5* iResolution.xy)/iResolution.y;

    vec3 col = vec3(0.);
    for(float i=0.,im=256.;i<im;i++){
    vec3 rnd = pcg3d(vec3(i));
    vec2 uuv = uv;
    uuv.y += (rnd.y-.5)*1.;
    float offx;
    uuv.x  += (offx=sin(i/im*6.28+iTime*(.1+i/im*.1)))*.6;
    float d = length(uuv)-.05*abs(cos(offx*1.75));
    //d= .01/(.01+max(0.,d));
    d= exp(-im*abs(d)*.25); 
    col+=mix(vec3(.2,.4,1.),vec3(1.,.4,.2),rnd.z)*d*exp(-3.*fract(iTime+i/im+rnd.z));
    }

    fragColor = vec4(min(vec3(1.,1.,1.),col+fwidth(col)),1.0);
}
