float scale = 3.;
//float anchorSize = 1.;
vec3 backColor[4] = vec3[](
    vec3(60. / 255., 163. / 255., 255. / 255.),  // Light blue
    vec3(30. / 255., 110. / 255., 200. / 255.),  // Medium blue
    vec3(10. / 255., 62. / 255., 150. / 255.),   // Dark blue
    vec3(1.)  // White
);
float PI = 3.14159265358979;
float a(vec2 p){
    float v = p.x + p.y;
    return sign(sin(v) + sin(v * 3. + PI));
}
float m2(float a) {
    return mod(a, 2.);
}
int anchor(vec2 p) {
    float t = iTime * 2.;
    float anchorSize = min((sin(t) + cos(t * 2.) + 2.), 1.);
    float block = 20. * scale;
    vec2 sp = p / block;
    vec2 tp = (sp - round(sp)) * block;
    float v = step(abs(tp.x), anchorSize * scale) * step(abs(tp.y), anchorSize * 3. * scale) + step(abs(tp.y), anchorSize * scale) * step(abs(tp.x), anchorSize * 3. * scale);
    return int(min(v, 1.));
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = fragCoord - iMouse.xy;
    float cellSize = 20. * scale;
    int idx = min(int(m2(m2(p.x / cellSize) + floor(m2(p.y / cellSize)))), 1);
    int ofs = int(min(0., a(p / cellSize / 4. * PI + PI / 16.)));
    idx = max(anchor(p) * 3 + ofs, idx);
    vec3 col = backColor[idx];
    fragColor = vec4(col, 1.0);
}
