float sdCircle (vec2 _pos, float r){ // SDF Circle func
    return length(_pos) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(fragCoord.x / iResolution.x,fragCoord.y / iResolution.y);
    uv.x *= iResolution.x/iResolution.y;
    vec2 circlePos = vec2(.5,0.5);

    float circle = sdCircle(uv - circlePos, 0.5);

    vec2 dir = normalize(uv - circlePos) + (cos(iTime/4.f) + 1.0f) / 2.0f;

    float tex1 = texture( iChannel0,uv - dir).x;
    float tex2 = texture( iChannel0,uv + dir).x;//moving tex

    float alpha = smoothstep(-0.5, circle, -0.43);

    float mask = (tex1 + tex2) / 2.0f;

    float maskAplha = mask * alpha;

    vec4 col = vec4(mix(maskAplha, alpha, alpha));
    //vec4 col = vec4(alpha);
    fragColor = col;
}
