renderpass.code:
#define SmoothBump(hi, lo, w, x) smoothstep(hi + w, hi - w, x) * smoothstep(lo - w, lo + w, x)
#define r iResolution.xy
#define t iTime
#define pi acos(-1.)
#define TAU (2.* pi)

void mainImage(out vec4 o, vec2 u) {
    float L, T = SmoothBump(4., 1.5, .8, acos(cos(t)));
    //T = 0.;

    u = 4. * (u - r / 2.) / r.y 
            + vec2(mix(0., 5., T), 0);

    L = length(u) * 5.;

    u = mix(
            vec2(
                log(length(u)), 
                atan(u.y, u.x)
            ), u,
            T
        );

    u = 12. * fract((u - t * .5) / TAU); 
    u = fract(u) - .5;

    o = vec4(1)
        - smoothstep(30. / r.y, .0,
            L * (max(abs(u.x), abs(u.y)) - .48));
}
