float f(float x, int iMax) {
  float wave = .0;
  float a = 1., b = 1.;
  for(int i = 0; i < iMax; i++) {
    wave += cos(x * a) * b;
    a *= 2.5;
    b *= .5;
  }
  return wave;
}

void mainImage(out vec4 O, in vec2 U) {
  float nx = (U.x * 2. - iResolution.x) / iResolution.y;
  float ny = U.y / iResolution.y - .5;

  int iMax = 13 - int(iMouse.x / iResolution.x * 12.);

  float wave = f(nx, iMax) * sin(nx + iTime * 2.);

  O = vec4(ny < wave * .2 ? 1. : .0);
}
