void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy - .5;
    float red = sin(5.*iTime + 45.*(uv.x * (uv.y + 2.)));
    float green = cos(14. + 3.*iTime + 45.*(uv.x * (uv.y + 4.)));
    float blue = cos(18. + 2.*iTime + 45.*(uv.x * (uv.y + 6.)));
	fragColor = vec4(red + blue, green + red, blue + red, 1.);
}
