#define rot(x) mat2(cos(x+vec4(0,11,33,0)))


//formula for creating colors;
#define H(h)  (  cos(  h/4. + 5.*vec3(1,3.*sin(iTime/5.+1.4),2.*sin(iTime/7.))   )*.8  )


//formula for mapping scale factor 
#define M(c)  log(c)

#define R iResolution

#define ex exp(w.x)
#define cy cos(w.y)
#define sy sin(w.y)
#define cz cos(w.z)
#define sz sin(w.z)
#define cw cos(w.w)
#define sw sin(w.w)


void mainImage( out vec4 O, vec2 U) {

    O = vec4(0);

    vec3 c=vec3(0);
    vec4 rd = normalize( vec4(U-.5*R.xy, .6*R.y, 0.));

    float dotp, totdist=0., tt=iTime, t=0.;

    for (float i=0.; i<50.; i++) {

        vec4 p = vec4( rd*totdist) ;

        p.z  += -3. +  sin(tt/5.);
        p.x += .5;
        p.y -= 10.;

        p.xz *= rot( 1.4*(1.+.2*sin(tt/3.) ) ) ;
        p.xy *= rot( -.6 * (1.+.2*sin(tt/3.)) ) ;
        p.yz *= rot(tt/5.);

        vec4 w=vec4(0) , dz, z;


        for (float j=0.; j<4.; j++) {

                z = vec4( 
                        ex*(cy*cz*cw - sy*sz*sw),
                        ex*(sy*cz*cw + cy*sz*sw),
                        ex*(cy*sz*cw - sy*cz*sw),
                        ex*(sy*sz*cw + cy*cz*sw)
                        );

                //iterated derivative here instead of just scale factor
                dz = vec4(z.x*dz.x-z.y*dz.y-z.z*dz.z-z.w*dz.w,
                          z.x*dz.y+z.y*dz.x+z.z*dz.w-z.w*dz.z,
                          z.x*dz.z-z.y*dz.w+z.z*dz.x+z.w*dz.y,
                          z.x*dz.w+z.y*dz.z-z.z*dz.y+z.w*dz.x);

                dz.x += 3. + sin(tt);

                dotp = max(1./dot(z,z),.008);

                dz *=  dotp;

                w = z *dotp + p ;

        }

        float ddz = clamp(dot(dz,dz),1e-3,1e5);
        float ddw = clamp(dot(w,w),1e-3,1e5);

        float dist = pow(ddw/ddz,.25) * log(ddw);

        float stepsize = dist/40.;     
        totdist += stepsize;

        c +=
             .1*
             H( log(ddz) ) * exp(-i*i*dist*dist*.3e-3); 
    }

    c = 1. - exp(-c*c);
    O = ( vec4(c,0) );

}
