//https://iquilezles.org/articles/palettes/
vec3 palette( float t ) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263,0.416,0.557);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // fragCoord: input vector - x & y coordinate of the current value
    // range: 900x1600 canvas: fragCoord will be (900,1600)
    //        we want the uv to range from -1 to 1, 
    //        so we normalize the fragCoord by dividing the resolution
    // iResolution: vec3(x,y,z), depth won't be used cuz 2D canvas
    // Swizzlng: v.zzx = vec3(v.z, v.x, v.y, v.y); 
    //           works with operations too when two vectors #component equals
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    // now the uv ranges from 0 to 1, with center being (0.5, 0.5). We want the center to be (0,0)
    // uv = uv * 2.0 - 1.0; //now the uv is centered at (0,0), ranges from (-1 to 1)
    // uv.x *= iResolution.x/ iResolution.y; //times the ratio to avoid stretching
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for (float i = 0.0; i < 2.0; i++) {
        uv = fract(uv * 1.5) - 0.5; //center the repeated uv

        float d = length(uv) * exp(-length(uv0));
        vec3 col = palette(length(uv0) + iTime * 0.4);
        d = sin(d * 8. + iTime)/8.; //sdf for 2D circle
        d = abs(d);
        d = 0.02 / d;

        finalColor += col * d;
    }

    fragColor = vec4(finalColor,1.0);        // fragColor: output param - rgba, each channel ranges from 0-1  
}
