#define PI 3.14159265359
#define S 5.
#define A 0.1
#define K 3.
#define FREQ 20.

// taken from thebookofshaders.com
mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

// taken from thebookofshaderx.com
mat2 scale(vec2 _scale){
    return mat2(_scale.x,0.0,
                0.0,_scale.y);
}

vec2 cartesianToPolar(vec2 cartesian) {
    float r = length(cartesian);          
    float theta = atan(cartesian.y, cartesian.x); 
    return vec2(r, theta);  

}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

   vec2 uv = fragCoord / iResolution.xy;
   uv.x *= iResolution.x/iResolution.y;


    // ### PLAY WITH SPACE START ### move space from the center to the vec2(0.0)
    // Remap the space to -1. to 1 in a function of time
    uv = uv * 2. - 1.;


    //uv = cartesianToPolar(uv);
    vec2 waveCenter = vec2(0. + sin(iTime),0. + cos(iTime * 1.2));
    // distance of a given point from center of the wave
    float disFromWaveCenter = distance(uv, waveCenter);
    float wave =  A * sin(disFromWaveCenter * FREQ * sin(iTime) - iTime * S);
    uv = uv + normalize(uv) * wave * K;

    // probe texture
    vec3 textureColor = texture(iChannel0, uv).rgb;

    fragColor = vec4(textureColor, 1.0);
}
