float inlerp(float a, float b, float value)
{
    return clamp((value - a) / (b - a), 0.0, 1.0);
}

vec3 setRoadTexture(int column, vec2 uv, bool blinking)
{
    vec3 tex = vec3(0.0);

    switch(column)
    {
        case 1:
            float blink = (sin(iTime * 3.0) + 1.0) / 2.0;
            tex = mix(tex, texture(iChannel0, uv).xyz, blinking ? blink : 1.0);
            break;
        case 2:
            blink = (sin(iTime * 5.0) + 1.0) / 2.0;
            tex = mix(tex, texture(iChannel1, uv).xyz, blinking ? blink : 1.0);
            break;
    }
    return tex;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float laneCount = 3.0;
    float yellowLineWidth = 0.025;
    float yellowLinePos = 0.4625;

    laneCount *= 2.0;

    float w = yellowLineWidth + ((0.5 - yellowLinePos)- yellowLineWidth);
    vec2 iuv = uv;
    iuv.x = inlerp(w, 1.0 - w, iuv.x);
    iuv.x = clamp(iuv.x, 0.0, 0.9999);

    int lid = int(floor(iuv.x * laneCount));   
    vec2 tuv = iuv;
    tuv.x = fract(tuv.x * laneCount);

    vec3 col = setRoadTexture(lid, tuv, true);

    fragColor = vec4(col,1.0);
}
