////////////////////////////////////////////////////////////////////
#define pi acos(-1.)
#define deg pi/180.
#define time iTime*pi/10.
#define R iResolution.xy
#define ar R.x/R.y
vec3 cs = vec3(1.,2.,3.);
mat2 r2d(float a) {
    return mat2(cos(a),sin(a),-sin(a),cos(a));
}
vec3 c1(vec2 uv, float d, float s, float t) {
    vec3 col = vec3(0.);
    uv *= r2d(t);
    uv.y = abs(uv.y)-d;
    col += smoothstep(s,0.,length(uv));  
    return col;
}
vec3 c2(vec2 uv, float t) {
    vec3 col = vec3(0.);
    for (int i=0;i<20;i++) {
        float ii = float(i);
        uv *= r2d(t);
        uv *= 1.1;
        col += c1(uv,0.45-ii*0.01,sin(ii+t*40.)*0.01+0.02,t+ii*0.1);
    }
    return col;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 tv = uv;
    uv -= 0.5;
    uv.x *= ar;
    vec3 col = vec3(0.);
    for (int i=0;i<25;i++) {
        float ii = float(i+1);
        uv *= 1.+sin(time*3.+ii)*0.05;
        uv *= r2d(-time*0.1);
        col += smoothstep(0.1,0.,length(uv)*(0.2/(ii*0.2))+abs(uv.x*uv.y)*(4.+ii*0.1))*0.05;
        col += c2(uv,time+ii*0.002)*(sin(cs+ii/8.+time)*0.5+0.5);
    }
    fragColor = vec4(col,1.0);
}
