void mainImage( out vec4 O, vec2 U ){

    vec2 r = iResolution.xy;
    vec2 uv = U/r;
    vec2 cuv = (2.*U-r)/r.y;
    vec2 muv = (2.*iMouse.xy-r)/r.y;

    float zoom = exp(mod(iTime*5., 4.) + 6.);

    cuv /= zoom;
    cuv.x -= 1.4845834;


    int i = 0;
    vec2 z = cuv;
    for (; i < 1024; i++){
        z = vec2(z.x*z.x - z.y*z.y, z.x*z.y*2.) + cuv;
        if (dot(z,z) > 4.) break;
    }

    O = vec4(exp(-float(i)/pow(zoom, .275)*.05));
}
