#define MAX_STEPS 300
#define MAX_DIST 400.
#define SURF_DIST .0001

float sdSphere(vec3 p, float s) {
    return length(p) - s;
}

vec3 rotateY(vec3 p, float a) {
    float c = cos(a), s = sin(a);
    return vec3(p.x * c - p.z * s, p.y, p.x * s + p.z * c);
}

float getDistance(vec3 p) {
    float d = MAX_DIST;
    vec3 z = p;
    float scale = 1.0;

    for(int i=0; i<10; i++) {
        z = rotateY(z, iTime * 0.1 + iMouse.x * 0.001); // Añadir interacción con el ratón
        z = abs(z) - vec3(1.0, 0.5, 1.0) * scale;

        if((z.x) < z.y) z.xy = z.yx;
        if(z.x < z.z) z.xzy = z.xzy;
        if(z.y < z.z) z.xzy = z.xzy;

        z = z * 1.0 - vec3(1.0, 1.0, 0.5) * scale;
        scale *= 0.4;

        float sphereDist = sdSphere(z, 1.50 * scale);
        d = min(d, sphereDist / scale);
    }

    return d * 0.5;
}

vec3 getNormal(vec3 p) {
    float d = getDistance(p);
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(
        getDistance(p-e.xyy),
        getDistance(p-e.yxy),
        getDistance(p-e.yyx));
    return normalize(n);
}

float rayMarch(vec3 ro, vec3 rd) {
    float dO = 0.;
    for(int i=0; i<MAX_STEPS; i++) {
        vec3 p = ro + rd * dO;
        float dS = getDistance(p);
        dO += dS;
        if(dO > MAX_DIST || dS < SURF_DIST) break;
    }
    return dO;
}

vec3 getColor(vec3 p) {
    vec3 col = vec3(0.5) + 0.5 * cos(iTime * 0.1 + p.xyx * 0.2 + vec3(0,2,4));
    return mix(vec3(30.8, 10.5, 0.22), vec3(0.2, 0.5, 0.8), col) * 0.3; // Hacer colores más translúcidos
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.05*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);

    vec3 ro = vec3(1, 0, -4);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    ro = rotateY(ro, iTime * 0.1 + iMouse.x * 0.001); // Añadir interacción con el ratón
    rd = rotateY(rd, iTime * 0.1 + iMouse.x * 0.001); // Añadir interacción con el ratón

    float d = rayMarch(ro, rd);

    if(d < MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = getNormal(p);
        vec3 r = reflect(rd, n);

        float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
        col = getColor(p);

        col *= dif;
        col += pow(max(dot(r, normalize(vec3(1,2,3))), 0.0), 8.0); // specular
        col *= refract(rd, n, 1.0 / 1.533); // Añadir refracción para simular la óptica de las burbujas de jabón
    }

    col = pow(col, vec3(.4545));  // gamma correction

    fragColor = vec4(col,0.5); // Ajustar la opacidad
}
