#define TAU 6.28318530717958647693
#define E   2.71828182845904523536
#define PHI 1.618033988749894

vec2 iexp(float i) {
    return vec2(cos(i), sin(i));
}

vec2 cmul(vec2 a, vec2 b) {
    return vec2(a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x);
}



float osc(vec2 uv, vec2 p, vec2 f, float t) {
    float r = length(p-uv);
    float a = 1./(1.+5.*r);
    return a*cos((f.y + f.x*r)*a - t);
} 

float s(float x) {
    return 0.5 + 0.5*cos((x+0.5)*TAU);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = 2.0*uv - vec2(1.0);
    uv.x *= iResolution.x / iResolution.y;

    uv *= 0.4 + 0.2*length(uv);

    float t = 1.*iTime*TAU;
    const float T = 0.005;

    float v = 0.;
    const float N = 5.;
    for (float i = 0.; i < N; i++) {
        float f = TAU*i/N;

        float u = fract(T*t); // time parameter of animation
        float k = min(i, u*N) / N; // position of guy on range
        float q = max(0., u*N-i) / (N-i); // time parameter of animation out
        u *= 2.*u;

        float l = 0.5 + 0.25*u; // maximum range of guys

        float x = mix(k*l, 0.0, q*q);
        vec2 p = x * iexp(u*TAU + s(q)*f);

        if (fract(0.5*t*T) > 0.5) {
            v += osc(uv, p, vec2(300, s(q)*f), t);
            v += osc(uv, -p, vec2(300, s(q)*f), t);
        } else {
            v += osc(uv, p.yx, vec2(300, s(q)*f), t);
            v += osc(uv, -p.yx, vec2(300, s(q)*f), t);
        }
    }
    v /= 2.*N;
    v = v;

    vec3 n = normalize(vec3(dFdx(v), dFdy(v), v/iResolution.x));
    float s = max(0.0, dot(normalize(vec3(-uv, 1.)), n));

    vec3 lamb = vec3(0.949,0.510,0.008);
    vec3 spec = vec3(0.792,0.267,0.722);
    vec3 ambi = vec3(0.533,0.149,0.788);

    fragColor = vec4(lamb*v + spec*s + (length(uv)/10.)*ambi, 1.0);
    fragColor = vec4(v+s);
}
