// references
// https://www.shadertoy.com/view/wlBBRy
// https://www.shadertoy.com/view/NscGDf

#define SAMPLES 4.0

float posterize_f(float src, float steps) { return float(floor(src * steps) / steps); }
vec3 posterize_f3(vec3 src, float steps) { return vec3(floor(src * steps) / steps); }

vec4 blur_radial(sampler2D tex, vec2 texel, vec2 uv, float radius)
{
    vec4 total = vec4(0);

    float dist = 1.0/SAMPLES;
    float rad = radius * length(texel);
    for(float i = 0.0; i<=1.0; i+=dist)
    {
        vec2 coord = (uv-0.5) / (1.0+rad*i)+0.5;
        total += texture(tex,coord);
    }

    return total * dist;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    vec3 vid = texture(iChannel0, uv).rgb;

    float col = vid.r * blur_radial(iChannel0, -uv, uv, 8.0).r;
    col = pow(col, abs(sin(iTime) * sin(iTime * 0.8)));
    col = 1.0 - posterize_f(col, 16.0) * 0.9;

    vid = vid + vec3(col);
    vid *= vec3(1.1 * abs(sin(iTime * 0.25)), 0.8, 1.2);

    fragColor = vec4(clamp(vid, 0.0, 1.0), 1.0);
}
