void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0, 0, 0);
    float iTimeScale = 2.71828 * iTime;
    float tau = 6.283058;

    int count = 6;
    int lumBias = 1;
    for (int i = 0; i < count; i++) {
        float iLum = 1.0 - pow(float(lumBias + i) / float(lumBias + count), 2.71828);
        float iF = tau * float(i) / 6.0;
        vec2 center = vec2(0.5, 0.5) + 0.25 * vec2(cos(iTimeScale + iF), sin(iTimeScale + iF));
        if (length(uv - center) <= 0.1) {
            col = vec3(iLum, iLum, iLum);
            break;
        }
    }

    // Output to screen
    fragColor = vec4(col, 1.0);
}
