void mainImage(out vec4 Q, in vec2 U) {
    R = iResolution.xy;
    //if (iFrame%4>0) discard;
    vec3 p = vec3(.5*R3.xy,-2.5*R3.x);
    vec3 d = normalize(vec3(2.*(U-.5*R)/R.y,9));
    p -= .5*R3;
    p.yz *= ei(-.08);
    d.yz *= ei(-.08);
    if (iMouse.z>0.) {
    p.yz *= ei(-iMouse.y/R.x);
    d.yz *= ei(-iMouse.y/R.x);
    p.xz *= ei(iMouse.x/R.x*6.2);
    d.xz *= ei(iMouse.x/R.x*6.2);
    } else {
    p.xz *= ei(-.5+.2*sin(.2*iTime));
    d.xz *= ei(-.5+.2*sin(.2*iTime));
    }
    p += .5*R3;
    p += d*1.001*cube(p,d,vec3(0),R3);

    vec3 no = vec3(0);
    vec3 nd = vec3(0);
    Q = vec4(0,0,0,0);
    float e = 1.;
    for (float i = 0.; i < 50.; i++) {

        if (length(p-clamp(p,vec3(0),R3))>0.) break;
        float k = 1e9;
        vec3 col = vec3(0);
        for (float x = -1.; x <= 1.; x++)
        for (float y = -1.; y <= 1.; y++)
        for (float z = -1.; z <= 1.; z++)
        {
            vec3 w = floor(p)+vec3(x,y,z);
            if (length(w-clamp(w,vec3(0),R3))>1e-3) continue;
            vec4 c = C(w);
            for (int o = 0; o < 4; o++) {
                if (c[o] < 0.)break;
                vec4 a = B(d3(c[o]));
                vec2 s = sphere(p-a.xyz,d,.75);
                float j = absmin(s.x,s.y);
                if (j > 0. && j < k) {
                    k = j;
                    vec3 no = normalize(p+d*j-a.xyz);
                    col = .2*e*abs(sin(3.*d3(c[o]).x/R3.x+vec3(1,2,3)));

                    nd = normalize(d+.5*no);
                }
             }
        }
        if (k > 10.) {
            float c = cube(fract(p),d,vec3(0),vec3(1));
            c = clamp(c,.01,4.);
            p += d*1.01*c;
        } else {
            Q.xyz += col;
            p += 1.001*d*k;
            d = nd; 

            e *= .7;
             if (e < .3) break;

        }
    }
    //Q = texture(iChannel3,(U)/R);

}

// 3D utilities 
#define N 5.
vec2 R;
vec3 vel (vec3 v) {return v*inversesqrt(1.+dot(v,v));}
//  [ [0,R/N]; [0,N*N]  ] < -- > [0,R]
#define Main void mainImage (out vec4 Q, in vec2 _U) {R = iResolution.xy;vec3 U = d3(floor(_U));
#define A(U) s3d(iChannel0,U)
#define B(U) s3d(iChannel1,U)
#define C(U) s3d(iChannel2,U)
#define D(U) s3d(iChannel3,U)
#define R3 vec3(floor(R/N),N*N)
vec4 hash(vec4 p4) {
    p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}
#define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))
bool building (vec3 U) {
    vec3 u = abs(U-vec3(.7,.5,.5)*R3);
    vec3 v = abs(U-vec3(.3,.5,.5)*R3);
    return (max(u.x,max(u.y,u.z))<.2*R3.y)||(max(v.x,max(v.y,v.z))<.2*R3.y);
}
bool cell (vec3 u) {
    return u.x>=0.&&u.y>=0.&&u.z>=0.&&u.x<1.&&u.y<1.&&u.z<1.;
}
float d1 (vec3 U) {
    U = floor(U);
    return U.x+U.y*R3.x+U.z*R3.x*R3.y;
}
vec3 d3 (float i) {
    return vec3(mod(i,R3.x) , mod(floor(i/R3.x),R3.y), floor(i/R3.x/R3.y));
}
vec2 d2 (vec3 U) {
    U = mod(U,R3);
    float i = d1(U);
    return vec2(mod(i,R.x),floor(i/R.x));
}
vec3 d3 (vec2 u) {
    u = floor(u);
    float i = u.x+u.y*R.x;
	return d3(i);
}

vec4 s3d (sampler2D T,vec3 U) {
    return texelFetch(T,ivec2(d2(floor(U))),0);
}
float absmin(float a, float b) {
    if (a<0.||(b>0.&&b<a)) return b;
    else return a;
}
vec2 sphere (vec3 oc, vec3 direction, float sphereRadius) {
    float a = dot(direction, direction);
    float b = 2.0 * dot(oc, direction);
    float c = dot(oc, oc) - sphereRadius * sphereRadius;
    float discriminant = b * b - 4.0 * a * c;
    if (discriminant <= 0.0) return vec2(-1); 
    float t1 = (-b - sqrt(discriminant)) / (2.0 * a);
    float t2 = (-b + sqrt(discriminant)) / (2.0 * a);
    return vec2(t1, t2); 
}

float cube(vec3 p, vec3 d, vec3 lo, vec3 hi) {
    vec3 tLo = (lo - p) / d;
    vec3 tHi = (hi - p) / d;
    vec3 tMin = min(tLo, tHi);
    vec3 tMax = max(tLo, tHi);
    float tEntry = max(tMin.x, max(tMin.y, tMin.z));
    float tExit = min(tMax.x, min(tMax.y, tMax.z));
    if (tEntry > tExit || tExit < 0.0) return -1.0; 
    return tEntry > 0.0 ? tEntry : tExit;
}
vec4 LN (sampler2D tx, vec3 u) {
                    vec3 f = floor(u);
                    vec3 c = ceil(u);
                    #define G(_) texelFetch(tx,ivec2(d2(_)),0)
                    vec4 _000 = G(vec3(f));
                    vec4 _001 = G(vec3(f.xy,c.z));
                    vec4 _010 = G(vec3(f.x,c.y,f.z));
                    vec4 _011 = G(vec3(f.x,c.yz));
                    vec4 _100 = G(vec3(c.x,f.yz));
                    vec4 _101 = G(vec3(c.x,f.y,c.z));
                    vec4 _110 = G(vec3(c.xy,f.z));
                    vec4 _111 = G(vec3(c));
                    vec4 _00 = mix(_000,_001,fract(u.z));
                    vec4 _01 = mix(_010,_011,fract(u.z));
                    vec4 _10 = mix(_100,_101,fract(u.z));
                    vec4 _11 = mix(_110,_111,fract(u.z));
                    vec4 _0 = mix(_00,_01,fract(u.y));
                    vec4 _1 = mix(_10,_11,fract(u.y));
                    return mix(_0,_1,fract(u.x));
                }

