       vec4 c = C(floor(Q.xyz)+vec3(x,y,z));
       for (int k = 0; k < 4; k++) if (c[k]>0.) {
            vec3 u = d3(c[k]);
            vec4 q = B(u);
            //vec4 v = A(u);
            vec3 r = q.xyz-Q.xyz;
            float l = length(r);
            if (l>0.)
            f -=  .1*r/l*exp(-l);

        } else break;
        vec3 u = vec3(x,y,z);
        if (length(u)>0.){
            vec4 d = D(Q.xyz+u);
            f += .000002*d.w*(400.-d.w)*u*exp(-.5*dot(u,u));

        }

    }
    V.xyz = mix(V.xyz,D(Q.xyz).xyz,.05);
    V.xyz += f;


    if (R3.x-Q.x<2.) V.x -= 2.-R3.x+Q.x;
    if (R3.y-Q.y<2.) V.y -= 2.-R3.y+Q.y;
    if (R3.z-Q.z<2.) V.z -= 2.-R3.z+Q.z;

    if (Q.x<2.) V.x += 2.-Q.x;
    if (Q.y<2.) V.y += 2.-Q.y;
    if (Q.z<2.) V.z += 2.-Q.z;


    if (iFrame < 1) {

        V = vec4(-.01*sign(U.x-.5*R3.x),0,0,0);

    }



    Q = V;

}

