void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	fragColor = texture(iChannel1,fragCoord/iResolution.xy);
}

const float innerRadius = 4.;
const float outerRadius = 9.;
const float growth = 0.55;
const float decay = 0.9;
const float alpha = 0.5;
const float beta = 0.26;
const float gamma = 0.46;
const float delta = 0.27;
const float epsilon = 0.36;

vec2 getConv (vec2 uv) {
    float ui = 0.;
    float uo = 0.;
    float uic = 0.;
    float uoc = 0.;

    for (float dx = -outerRadius; dx++ <= outerRadius;) {
        for (float dy = -outerRadius; dy++ <= outerRadius;) {
            float uis = step(length(vec2(dx, dy)), innerRadius);
            float uos = step(length(vec2(dx, dy)), outerRadius);

            if (uis + uos < 1.) continue;

            float val = texture(iChannel1, uv + vec2(dx, dy) / iResolution.xy).r;

            ui += val * uis;
            uic += uis;

            uo += val * uos;
            uoc += uos;
        }
    }

    return vec2(ui / uic, uo / uoc);
}

float life (vec2 uv) {
    float v = texture(iChannel1, uv).r;
    vec2 convolutions = getConv(uv);
    float ui = convolutions.x;
    float uo = convolutions.y;

    bool condition = (ui >= alpha && uo >= beta && uo <= gamma) 
        || (ui < alpha && uo >= delta && uo <= epsilon);

    v += float(condition) * growth 
        - float(!(condition)) * decay;

    return clamp(v, 0., 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;   
    bool progStart = iFrame > 4;

    fragColor = (progStart && iFrame % 3 != 0)
        ? vec4(vec3(texture(iChannel1, uv).r), 1.0)
        : progStart
            ? vec4(vec3(life(uv)), 1.0)
            : texture(iChannel0, uv);
}
