void mainImage(out vec4 c, in vec2 v) {  // Simple Grass.
    v += fract(0.3 * v * fract(v));      // Add fractal chaos.
    v *= fract(v + fract(v));            // Fractal multiply.
    v.x -= 2.1;                          // Translate away symmetry.
    c = vec4(0, fract(v.x * v.y), 0, 1); // Show grass.
}                                        // By Rik Riesmeijer.
