#define R3D(a) mix(q*dot(p,q), p, cos(a)) + sin(a)*cross(p,q)
#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,s=.2; vec3 p,q=vec3(-1),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e);
    p.zx*=R2D(iTime*.2);

    for(j=0.;j++<16.;)
        p=abs(abs(p=R3D(i/j))-.15)-.01,
        d=min(max(p.z,p.y),p.x),
        d-=dot(length(p.zy),dot(p,q))/2e4;

    e-=d*cos(iTime);

}o+=vec4(exp(p),1)*3./i;
}
