#define PI 3.14159265358

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 z = fragCoord / iResolution.xy - vec2(0.5, 0.5);

    float t = iTime / 2.5;
    z = vec2(length(z), abs(sin(z.x) * sin(t)));

    float scale = 1.;
    for (int i = 0; i < 8; i++) {
        vec2 w = z * 2.;
        if (z.x >= 0.5 && z.y <= 0.5) { w.x-=1.; }
        else if (z.y >= 0.5 && z.x <= 0.5) { w.y-=1.; }

        scale*=2.;
        z = w;
    }
    float d = min(min(z.x, z.y), abs(1.-z.x-z.y))/scale;
    vec3 col = vec3(1.-smoothstep(0., 0.005, d));
    fragColor = vec4(col, 1.);


}
