const float GRIDSIZE  = 64.0;
const float GRIDWIDTH = 0.1;
const vec2 GRIDFIX = vec2(1.6,0.9);
#define Sqrt3 sqrt(3.0)
float hexDist( vec2 p)
{
    p = abs(p);
    float edgeDist = dot(p, normalize(vec2(1.0, Sqrt3)));
    return max(edgeDist, p.x);
}
float saturate(float p)
{
    return clamp(p,0.0,1.0);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * GRIDSIZE;

    vec2 rep = vec2(1.0, Sqrt3)*GRIDFIX.yx;
    vec2 hrep = 0.5 * rep;
    vec2 a = mod(uv, rep) - hrep;
    vec2 b = mod(uv - hrep, rep) - hrep;
    vec2 hexUv = dot(a, a) < dot(b, b) ? a : b;
    vec2 cellId = uv - hexUv;

    vec2 sampleUv = cellId / GRIDSIZE;
	vec4 tex = texture(iChannel0, sampleUv);
    float brightness = (tex.r +tex.g+tex.b)*0.333333;
	brightness =mix(0.75,0.35,saturate(brightness));
	float hex = 1.0-hexDist(hexUv*GRIDFIX.xy);
	tex*=smoothstep(saturate(brightness-GRIDWIDTH),saturate(brightness+GRIDWIDTH),saturate(hex));

    fragColor = tex;
}
