#define ITER 50

vec3 palette(float t, vec3 b,  vec3 s, vec3 d, vec3 m) {
  return b + s * cos(6.2831853071795864 * (t * m + d));
}

vec3 render(vec2 uv) {
  // viewport transform
  vec2 c = uv * 3.5 - vec2(.5, 0);

  // iterate
  vec2 z = c;
  for (int i = 0; i < ITER; i++) {
    z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + c;

    // orbit trap
    float a = abs(z.y + sin(iTime * .5) * 5.);
    float b = abs(z.x * cos(iTime * .5));
    if (a * b > 10.0) {
      vec3 col = vec3(1.0, abs(sin(iTime)), sin(uv.x + uv.y) + 1.0);
      return 4.0 * float(i) / float(ITER) * col;
    }
  }

  // inner part
  float val = cos(z.x + z.y) + .5;
  return palette(val * .6 + 1.2, vec3(0.50,0.50,0.50), vec3(0.50,0.50,0.50), vec3(0.47,0.57,0.67), vec3(0.90,0.90,0.90));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  // uvx2
  vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.x;

  // anti-aliasing
  #define SIZE 2
  vec3 col = vec3(0.0);
  float sub = 2. / iResolution.x / float(SIZE);
  for (int i = 0; i < SIZE; i++) {
  for (int j = 0; j < SIZE; j++) {
    col += render(uv + vec2(i, j) * sub);
  }}
  col /= float((SIZE) * (SIZE));


  // output to screen
  fragColor = vec4(col, 1.0);
}
