float sdSphere(vec3 p, float s)
{
    return length(p) - s;
}

float sdBox(vec3 p, vec3 b)
{
    vec3 q = abs(p) - b;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
}

float smin(float a, float b, float k)
{
    float h = max(k - abs(a - b), 0.0) / k;
    return min(a, b) - h*h*h*k*(1.0 / 6.0);
}


float map(vec3 p)
{

    float sphere;
    float totalSpheres = 1.0;

    for (float i = 0.0; i < 20.0; i += 1.0)
    {
        vec3 spherePos = vec3(-3.0 + i * 0.35, sin(iTime * 3.0) * 0.1 * i, sin(iTime) * 0.03 * i);


        sphere = sdSphere(p - spherePos, 1.0);
        totalSpheres *= sphere;
    }



    float ground = p.y + 0.75;

    return smin(ground, totalSpheres, 1.0);

}






void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    //Put UV coords into clip space
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    vec3 rayOrigin = vec3(0, 0, -3);
    vec3 rayDir = normalize(vec3(uv, 1));
    vec3 col = vec3(0);

    float totalDist = 0.0;

    //Raymarching

    for (int i = 0; i < 80; i++)
    {
        vec3 rayPos = rayOrigin + rayDir * totalDist; //Position along the ray

        float d = map(rayPos); //current distance to the scene

        totalDist += d; //March the ray one step

        col = vec3(i) / 80.0;

        if (d < 0.001 || totalDist > 100.0) break;
    }

    fragColor = vec4(col, 1);
}
