// More information at: CreativeCommons.org/publicdomain.

void mainImage(out vec4 c, vec2 v) {            // Simple Hex Grid.
    v *= .01;                                   // Resize canvas.
    v.y *= 1.15;                                // Vertical scale adjust.
    v.x -= fract(floor(v.y) / 2.);              // Odd row tile shift.
    v = abs(fract(v) - .5);                     // A center symmetry.
    c = abs(1. - v - max(v.y * 1.5, v.x)).xxxx; // Widen cell center.
    c.b = .3;                                   // Happy Bumblebees.
}                                               // By Rik Riesmeijer.
