// code ref: https://www.shadertoy.com/view/MdVfRR

float PI = 3.14159265359,
    arms = 10.,
   speed = 1.5,
   width = .4,
subdivide = 6.;
float slowchangecos(float time){
    return 1.0 - cos((time * PI) / 5.0);
}
vec3 col( float t ) {

    vec3 a = vec3(0.506,0.645,0.812);
    vec3 b = vec3(0.507,0.615,0.800);
    vec3 c = vec3(0.500,0.628,0.624);
    vec3 d = vec3(0.1,0.3,0.5);
    a = mix(a, c, slowchangecos(iTime));

    vec3 timePhase = vec3(1.0, 2.0, 3.0) * t;

    return a + b*cos( 2.0*(c*timePhase+d) );
}

void Koleido(inout vec2 uv, float ka)
{
  float angle = abs(mod (atan (uv.x, uv.y), 0.8 * ka) - ka )*(1.+sin(iTime * PI / 8.));
  uv = length(uv) * vec2(sin(angle), cos(angle));
}
void mainImage(out vec4 O, in vec2 fragCoord)
{
    vec2 uv = ( fragCoord - .5* iResolution.xy ) / iResolution.y;
    vec2 mp = ((uv.xy + iMouse.xy) * 0.5) / iResolution.xy;
    uv *= 0.2 + 10.0 * mp.x;
    float sideCount = 1.0 + round(mp.y * 8.0);
     Koleido (uv, PI / subdivide);
    float len = length(uv),
   		angle = (atan(uv.x,uv.y)/(2.*PI) * sin(iTime * PI / 10.0))+1.5,
    	wobble = 6.+4.*abs(cos(iTime/5.)),
    	white = fract((angle)*arms+sin((sqrt(len)*wobble)-iTime*speed));

    white  = 2.*sin(white/(PI/10.));
    white *= floor(fract(angle*arms+sin(iTime/speed-(len*1.2)*wobble))*subdivide)/subdivide;

    vec4 color = vec4(col(2.0* sin(iTime * PI * uv.x / 5.)+iMouse.y*2.), 1.0);
    O = smoothstep(0.,1.,white*color);

    vec4 levels = vec4(4,8,16,1);
    O = floor(O*levels)/levels;
}
