//philip.bertani@gmail.com
//volumetric raymarch technique learned from gaz and elsio

//fabriceneyret2 rotation formula
#define rot(x) mat2(cos(x+vec4(0,11,33,0)))

//Rodrigues-Euler axis angle rotation
#define ROT(p,axis,t) mix(axis*dot(p,axis),p,cos(t))+sin(t)*cross(p,axis)

//formula for creating colors;
#define H(h,d)  (  cos(  h + vec3(70,10.+5.*sin(iTime),3)   )*.7 + .5 )

//formula for mapping scale factor 
#define M(c)  2.*log(1.+c)

#define R iResolution


void mainImage( out vec4 O, vec2 U) {

    O = vec4(0);

    vec3 c=vec3(0);

    vec4 rd = normalize( vec4(U-.5*R.xy, R.y, R.y*2.))*80.;  //use 4 coordinates

    float sc,dotp,totdist=0., tt=iTime;

    for (float i=0.; i<200.; i++) {

        vec4 p = vec4( rd*totdist);

        //mix 3d subspaces
        p.yzw = ROT(p.xyz+vec3(0,0,-1.5), normalize( vec3(sin(17.1/2.), sin(17.1), cos(17.5/3.) )  ), 3.83);

        sc = 1.;  //scale factor

        p.xz = cos(p.xz/5.);   //causes radial blur by smearing space in line with observer

        p.yz *= rot(iTime+cos(iTime));


        for (float j=0.; j<8.; j++) {

            p = abs(p)*.89;

            dotp = max(1./dot(p,p),.05);
            sc *= dotp ;

            p = abs(p) * dotp - .33 ;
        }

        float dist = abs( length(p)-.05)/sc ;  //funky distance estimate
        float stepsize = dist/20. ;     
        totdist += stepsize;              //move the distance along rd

        //accumulate color, fading with distance and iteration count
        c += mix( vec3(1), H(M(sc),id),.9) *.03*  exp(-i*i*stepsize*stepsize*1e3);
    }

    c = 1. - exp(-c);
    O = ( vec4(c,0) );

}
