void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)并且防止出现横纵位置不一致
    //vec2 uv = (fragCoord - 0.5* iResolution.xy) / iResolution.xx;
    //vec2 uv = (2.*fragCoord - iResolution.xy) / iResolution.xx;
    vec2 uv =(2.*fragCoord - iResolution.xy) / min(iResolution.x,iResolution.y);
    float c=0.;
    float r=0.3;
    if(length(uv) < r){
        c=1.;
    }

    // Output to screen
    fragColor = vec4(vec3(c),1.0);
}
