#define PI 3.14159
#define BPM 175.0
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 circ = fragCoord.xy - iResolution.xy * 0.5;
    vec3 col = vec3(0,0,1);

    float mps = BPM / (60.0 * 4.0);
    float rotation = (iTime * mps * 2.0 * PI) - 0.5 * PI;

    //[0, 2PI]
    float angle = mod(atan(circ.y, circ.x) + rotation, 2.0 * PI);
    float value = smoothstep(0.0, 2.0 * PI, angle);
    col *= value;

    // Output to screen
    fragColor = vec4(col,1.0);
}
