vec3 palette( float t ) {
    vec3 a = vec3(0.5, 0.25, 0.5);
    vec3 b = vec3(0.25, 0.15, 0.75);
    vec3 c = vec3(0.5, 0.25, 0.75);
    vec3 d = vec3(0.25, 0.25, 1.5);

    return a + b * cos (6.28318 * (c * t + d));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for (float i = 0.0; i < 6.0; i++) {
        uv = fract(uv * 1.61803) - 0.5; // + sin(iTime*.1)*2.;

        float d = length(uv) * exp(-length(uv0));

        vec3 col = palette(length(uv0) + i*0.4 + iTime);

        d =sin(d*8. + iTime)/8.;
        d = abs(d);

        d = pow(0.01 / d, 1.5);
        //d += smoothstep(1.0, 2.5, d);

        finalColor += d * col ;
}

    //fragColor = vec4(0, uv.y + 1.0 / 2.0, cos(d), 1);
    fragColor = vec4(finalColor, 1.);
    //fragColor = vec4(uv.x+d, uv.y + 1.0 / 2.0, 0, 0);
    //fragColor = vec4(col, 1.);
}
