// Breath in as the ball raises
// Breath out as the ball falls
// Push the air down your tummy
// Maintain a constant air flow

#define BREATH_SPEED 5.0 // decrease if it's too slow

#define BALL_SIZE    0.2

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // make oscilation between 0.0 and 0.5
    float tosc = cos(iTime * 3.1415 / (BREATH_SPEED * 2.0));
    float osc = pow(tosc, 2.0);
    // move ball position
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    p.y += 0.5 -  osc;
    // circle sdf
	float d = length(p) - BALL_SIZE;
    vec3 col = smoothstep(0.2,.02,d) * vec3(0,2,4);
    // update color itensity per cycle
    fragColor = (1.2-osc)*vec4(col,1.);
}
