const float PI=acos(-1.);
const float TAU=PI*2.;

float ease(float n){
return smoothstep(0.,1.,n);
}
float loader(vec2 p, float t){
float px=1.5/iResolution.y;

    float donut=1.-smoothstep(.1,.1+px,abs(length(p)-.3));
    float angle=atan(p.y,p.x)/TAU+.5;
    angle=fract(angle+t);
    float pie=smoothstep(.1,.1+px,abs(angle)-ease(sin(t*3.))*.2-.3);

    return pie*donut;

}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x*=iResolution.x/iResolution.y;
    uv-=vec2(iResolution.x/iResolution.y,1.)*.5;

    float t=iTime*.4;

    vec2 r_uv=uv+ease(sin(t*.5)*.5+.5)*.05;
    vec2 g_uv=uv+vec2(1.,-1.)*ease(sin(t*.5+PI)*.5+.5)*.05;
    vec2 b_uv=uv+
    sin(
    uv*12.+vec2(.3+t*3.,.7+t*3.)



    )*.1;

    float lsd=1.;//step(3.,mod(floor(t*3.),4.));

    vec3 col=vec3(
    loader(mix(uv,r_uv,lsd),t),
    loader(mix(uv,g_uv,lsd),t),
    loader(mix(uv,b_uv,lsd),t)
    );

    // Output to screen
    fragColor = vec4(vec3(1.)-col,1.0);
    //fragColor=texture(iChannel0,fragCoord/iResolution.xy+sin(fragCoord*12./iResolution.xy)*.1);
}
