void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5 *iResolution.xy)/iResolution.y;

    vec3 col =vec3(0);
    vec2 gv = fract(uv*40.)-0.5; 


    float m = 0.;

    float t = iTime*-1.1;

    float dist = length(uv)*5.;

    for(float y=-1.; y<=1.;y++)
    {
      for(float x=-1.; x<=1.;x++)
      {
        vec2 offs = vec2(x,y);
        float d = length(gv+offs);


        float r = mix(.01,.7,sin(t+dist*3.)*.5+.5);
        m+= smoothstep(r,r*.99999,d);
        }
      }


    //col.rg = gv;

    col += mod(m,40.);
    col.r += -.1;
    col.g += -.5;
    col.b *= -.5;



    vec2 uv2 = (fragCoord-.5 *iResolution.xy)/iResolution.y;

    vec3 col2 =vec3(0);
    vec2 gv2 = fract(uv*50.)-0.5; 


    float m2 = 0.;

    float t2 = iTime*-1.3;

    float dist2 = length(uv)*5.;

    for(float y=-1.; y<=1.;y++)
    {
      for(float x=-1.; x<=1.;x++)
      {
        vec2 offs = vec2(x,y);
        float d = length(gv+offs);


        float r = mix(.02,.6,sin(t+dist*3.)*.5+.5);
        m+= smoothstep(r,r*.99999,d);
        }
      }


    col2 += mod(m,40.);
    col2.r += -.12;
    col2.g += -.1;
    col2.b *= -.15;











    fragColor = vec4(col/col2*2.,.5);
}
