#define time iTime
#define resolution iResolution.xy

float rand(int seed, float ray) {
	return mod(sin(float(seed)*1.0+ray*1.0)*1.0, 1.0);
}

float happy_star(vec2 uv, float anim)
{
    uv = abs(uv);
    vec2 pos = min(uv.xy/uv.yx, anim);
    float p = (2.0 - pos.x - pos.y);
    return (2.0+p*(p*p-1.5)) / (uv.x+uv.y);      
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	float t = time;
	vec2 r = resolution;
    float scale3 = resolution.y / 50.0;
	float ring = 20.0;
	float radius = resolution.x*1.0;
	float gap = scale3*1.36;
	vec2 pos = gl_FragCoord.xy - resolution.xy*.5;

	float d = length(pos);

	// Create the wiggle
	d += 0.5*(sin(pos.y*0.25/scale3+time)*sin(pos.x*0.25/scale3+time*.5))*scale3*5.0;

	// Compute the distance to the closest ring
	float v = mod(d + radius/(ring*2.0), radius/ring);
	v = abs(v - radius/(ring*2.0));

	v = clamp(v-gap+d, 0.0, 1.0);

	d /= radius;
	vec3 m = fract((d-1.0)*vec3(ring*-.5, -ring, ring*.125)*0.5);

	vec2 p = gl_FragCoord.xy / resolution.xy-.5;
      float rotZ = 1. - 0.13 * sin(1. * cos(length(p * 1.)));
    p *= mat2(cos(iTime), sin(iTime), -sin(iTime), cos(iTime));
     p *= mat2(cos(rotZ), sin(rotZ), -sin(rotZ), cos(rotZ));
	p = p * 10.0;
	p.x *= resolution.x / resolution.y;	
	vec3 destColor = vec3(0.0);
	for (float i = 1.0; i<40.0;i++){
		float j = i;
        float angle = time + i / 10. * 6.28;
	    p *= mat2(cos(iTime), sin(iTime), -sin(iTime), cos(iTime));

		vec2 q =  p + vec2(cos(t+v * j), sin(t * j)) * 3.63;
          q *= mat2(cos(rotZ), sin(rotZ), -sin(rotZ), cos(rotZ));
		float l = 0.22 / abs(length((q)+d*q)*1.5*i*abs(tan(t) ));
		destColor +=  vec3(abs(tan(t))*l,abs(sin(t))*l,abs(cos(t))*l);
	}
	fragColor = vec4(destColor+vec3(0.2,0.,1.5),1.0);
     vec2 uv = ( fragCoord - .5*iResolution.xy ) / iResolution.y;
    uv *= 2.0 * ( cos(iTime * 2.0) -2.5); // scale
    float anim = sin(iTime * 12.0) * 0.1 + 1.0;  // anim between 0.9 - 1.1 
    fragColor+= vec4(happy_star(uv, anim) * vec3(0.35,0.2,0.35)*0.1, 1.0);
}
