/*codigo Shayder M@teus Henrique 24/07/2024 */

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // aqui eu coloquei as coordenadas normalizadas de 0 a 1.
    vec2 uv = fragCoord / iResolution.xy;

    // textura Ichannel0 é Ichannel1.
    vec4 videoColor = texture(iChannel0, uv); // Textura principal com fundo verde.
    vec4 backgroundColor = texture(iChannel1, uv); // Imagem de fundo iChannel1.

    // Parãmetros do cromakey.
    vec3 keyColor = vec3(0.0, 1.0, 0.0); // Chavear cor verde.
    float threshold = 0.7; // limite
    float softness = 0.00005; // suavizar a borda verde, maximo que consegui.

    // calculo da diferença entre pixel atual e cor main(masking)
    float diff = length(videoColor.rgb - keyColor);

    // Calcule o valor alfa com base na diferença e no parâmetro de suavidade.
    float alpha = smoothstep(threshold - softness, threshold + softness, diff);

    // Misture a cor da textura principal com a cor de fundo com base no valor alfa.
    fragColor = mix(backgroundColor,videoColor, alpha);
}
