void mainImage(out vec4 color, in vec2 coord) {

    vec2 uv = coord / iResolution.xy;
    color = texture(iChannel0, uv); 


}

void mainImage( out vec4 fragColor, in vec2 uv ){

    float a = floor(float(iFrame));

    if(mod(a,4.)<=0.){
    uv.x = 4.5*(uv.x-0.5*iResolution.x)/iResolution.y;
    uv.y = 4.5*(uv.y-0.5*iResolution.y)/iResolution.y;
    vec3 col = vec3(0.);
    float pi = 3.141592;

    float a = 1.25+mod(0.25*floor(float(iFrame)/100.),2.75);

    float s = 0.00125*1.;
    float n = 10000.;

    float ang = atan(uv.y/uv.x);

    for(float theta=0.; theta<=4.*3.1415; theta+=3.141592/60.){

    float r1 = 0.;
    r1+=cos(atan(cos(a*theta))-cos(60.*theta))+1.;

    float y1 = -r1*cos(theta);
    float x1 = r1*sin(theta);
    vec2 vec = vec2(x1,y1);
    float j=0.2;
    col+=min(1./length(uv-vec),n)*s*vec3(1.-j+j*sin(10.*a),1.-j+j*cos(10.*a),1.-j+j*cos(10.*a));

    r1 = 0.;
    r1+=cos(atan(-cos(a*theta))-cos(60.*theta))+1.;

    y1 = -r1*cos(theta);
    x1 = r1*sin(theta);
    vec = vec2(x1,y1);
    col+=min(1./length(uv-vec),n)*s*vec3(1.-j+j*sin(10.*a),1.-j+j*cos(10.*a),1.-j+j*cos(10.*a));



    }
    float g=0.05;
    for(float i=10.; i>-0.; i+=-g){
    if(length(col)>1.-i && length(col)<1.-i+g){
    col*=1./length(col)*pow(min(1.-i+g,1.),2.);
    }
    }



    fragColor = vec4(col,1.0);    

    }else{
    uv = uv / iResolution.xy;
    fragColor = texture(iChannel0, uv); 
    }



}
