vec3 black = vec3(0.0,0.0,0.0);
vec3 white = vec3(1.0,1.0,1.0);

bool level1(vec2 coords){
    bool x = mod(coords.x,2.0) > 1.0;
    bool y = mod(coords.y,2.0) > 1.0;
    return x == y;
}

vec3 dither(float brightness, vec2 coords){

    vec2 mouse = iMouse.xy/iResolution.xy;
    if(brightness > mouse.x)
    {
    return white;
    }
    else if( brightness < mouse.y)
    {
    return black;
    }
    else{
    return level1(coords) == true ? black : white;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float color = texture(iChannel0, uv).x;

    vec3 dith = dither(color,fragCoord);

    // Output to screen
    fragColor = vec4(dith,1.0);
}
