#define color_rect vec3(0.745,0.678,0.539)
#define color_a vec3(0.653,0.918,0.985)
#define color_b vec3(0.980,0.576,0.113)

vec4 rect(in vec2 uv, in float xPos){

    float thickness = 0.05;

    if (abs(uv.x - xPos) <= thickness && abs(uv.y - 0.5) < 0.3) {
       return vec4(color_rect, 1.0);
    }

    return vec4(vec3(1.0), 0.0);;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = mix(color_a, color_b, step(0.5, uv.x));

    vec4 a = rect(uv, 0.25);
    vec4 b = rect(uv, 0.75);

    col = mix(col, a.rgb, a.a);
    col = mix(col, b.rgb, b.a);

    fragColor = vec4(col,1.0);
}
