void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec3 data = texture(iChannel0, uv).rgb;
    float dist = distance(vec2(0.0), data.rg);

    fragColor = vec4(1.0);
    if (dist > 2.0) {
        fragColor = vec4(1.0/32.0) * data.b;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = fragCoord / iResolution.xy;
    float zreal = texture(iChannel0, uv).r;
    float zimag = texture(iChannel0, uv).g;
    float iter  = texture(iChannel0, uv).b;
    float dist = distance(vec2(0.0), vec2(zreal, zimag));

    if (dist > 2.0) {
        fragColor = vec4(2.0, 2.0, iter, 1.0);
        return;
    } else if (iter >= 32.0) {
        fragColor = vec4(zreal, zimag, iter + 1.0, 1.0);
        return;
    } else {
        vec2 c = 3.0 * uv - vec2(1.5); //scale
        c.x *= iResolution.x / iResolution.y; //aspect ratio fix
        c.x -= 1.0; //origin
        float real = zreal * zreal - zimag * zimag + c.x;
        float imag = 2.0 * zreal * zimag + c.y;
        fragColor = vec4(real, imag, iter + 1.0, 1.0);
        return;
     }
}
