vec2 square(vec2 num) {
    num = vec2(num.x*num.x-num.y*num.y,2.*num.x*num.y);
    return num;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x = 5.*uv.x - 2.5;
    uv.y = 3.*uv.y - 1.5;
    vec2 z = vec2(0.,0.);
    vec3 col = vec3(0.,0.,0.);
    int iterations = int(iTime);
    for(int i = 0;i < 255; i = i + 1) {
        z = square(z) + uv;
        if (i > iterations) {
            float tint = float(i)/8.0;
            col = tint*sin(iTime+vec3(0.,2.,4.));
            break;
        }
    }

    // Output to screen
    fragColor = vec4(z,0.0,1.0);
}
