// 参考: https://blog.narumium.net/2020/05/15/glsl%e3%81%a7%e7%94%bb%e5%83%8f%e3%81%ae%e3%81%bc%e3%81%8b%e3%81%97%e3%80%81%e3%83%a2%e3%82%b6%e3%82%a4%e3%82%af/
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 st = fragCoord/iResolution.xy;
    vec3 color = vec3(0.0);

    float num = iTime*10.0;
    st *= num;
    st = floor(st)/num;

    fragColor = texture(iChannel0,st);
}

// https://thebookofshaders.com/09/?lan=jp
float circle(in vec2 _st,in vec2 _pos, in float _radius){
    vec2 l = _st-_pos;
    return 1.-smoothstep(_radius-(_radius*0.01),
                         _radius+(_radius*0.01),
                         dot(l,l)*4.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 st = fragCoord/iResolution.xy;
    vec3 color = vec3(0.0);

    st *= 3.0;      // Scale up the space by 3
    st = fract(st); // Wrap around 1.0
    color = vec3(circle(st,vec2(0.000,0.000),3.));    
    vec3 color2 = vec3(circle(st,vec2(0.000,0.000),2.));
    vec3 color3 = vec3(circle(st,vec2(0.000,0.000),1.));
    vec3 color4 = vec3(circle(st,vec2(0.000,0.000),0.5));

    if(color4 == color){
	    fragColor = vec4(0.9,0.9,0.9,1.0);
	}else if(color3 == color){
       	fragColor = vec4(0.8,0.8,0.8,1.0); 
    }else if(color2 == color){
        fragColor = vec4(0.5,0.8,0.8,1.0); 
    }else {
		fragColor = vec4(1.0,1.0,0.0,1.0);        
    }
}
