void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = fragCoord / iResolution.xy;

  float audio = 0.5 * renderAudio( iTime + 0.01 * uv.x ).x;
  float dAudio = abs( dFdx( audio ) );

  float shape = linearstep( 2.0 / iResolution.y, 0.0, abs( uv.y - 0.5 - audio ) - 0.5 * dAudio );
  fragColor = vec4( vec3( shape ), 1.0 );
}

vec2 mainSound( int samp, float time ) {
  return renderAudio( time );
}

#define saturate(x) clamp(x,0.,1.)
#define linearstep(a,b,t) saturate(((t)-(a)) / ((b)-(a)))

const float PI = acos(-1.0);
const float TAU = PI * 2.0;

vec2 renderAudio(float time) {
  float env = exp(-10.0 * max(time - 0.2, 0.0));
  float fmamp = mix(
    2.0 + tanh(10.0 * sin(TAU * 80.0 * time)),
    2.0,
    smoothstep(0.1, 0.2, time)
  );

  float phase = 2600.0 * time;
  phase -= 16.0 * (1.0 + cos(PI * exp(-16.0 * time)));
  phase += 1.2 * sin(TAU * 8.0 * time);

  float wave = sin(fmamp * sin(TAU * phase));
  wave *= env;

  return 0.5 * vec2(wave);
}
