const float MAX_ITERATION = 9.0;
const float LINE_THICKNESS = 0.0055;

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy*2.0-iResolution.xy)/min(iResolution.x, iResolution.y);
    uv = fract(uv);

    int iteration;
    {
        vec2 luv = fragCoord.xy/iResolution.xy;
        vec3 texc = texture(iChannel0, luv).xyz;
        float intensity = 1.0-dot(texc, vec3(0.299, 0.587, 0.114));
        iteration = int(intensity*MAX_ITERATION);
    }

    vec2 cell_begin = vec2(0.0);
    {
        vec2 cell_uv = uv;
        vec2 cell_size = vec2(1.0);
        const vec2 boundary = vec2(0.5, 0.5);
        for(int i = 0; i < iteration; ++i)
        {   
            vec2 is_out_of_boundary = step(boundary, cell_uv);

            vec2 fcell_uv = fract(cell_uv);

            cell_uv = mix(fcell_uv/boundary, (fcell_uv-boundary)/(1.0-boundary), is_out_of_boundary);
            cell_begin += mix(vec2(0.0), boundary*cell_size, is_out_of_boundary);

            cell_size = mix(boundary, 1.0-boundary, is_out_of_boundary)*cell_size;
        }
    }

    vec2 line = step(cell_begin+LINE_THICKNESS, uv);
    vec3 color = mix(vec3(0.0, 0.0, 0.0), vec3(1.0, 1.0, 1.0), line.x*line.y);

    fragColor = vec4(color, 1.0);
}
