void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p,q=vec3(-1,0,1),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e);
    p=vec3(log2(length(p.yz))-tan(iTime*.5),p.z,atan(p.x,p.y));

    for(j=0.;j++<24.;)
        p=abs(mix(p,q,2e-4))-1.,
        d=min(min(p.z,p.x),p.y);

    e+=d/j;

}o+=vec4(.6*exp(p*q/d),1)/i;
}
