float seed = 628.41872;

vec2 rand(vec2 pos, float seed)
{
    float sinA = sin(seed);
    float cosA = cos(seed);
    float tanA = sinA/cosA;
    mat2 rot = mat2(cos(seed),-sin(seed),sin(seed),cos(seed));
    for(int i = 1; i < 10; i++)
    {
        pos = mod(rot*tan(pos + 1.5),abs(0.5*cos(seed)));
    }
    return normalize(pos);
}

float voronoi(vec2 pos, float cellSize)
{
    float dist = 10.0;
    for(int x = -1; x <= 1; x++)
    {
        for(int y = -1; y <= 1; y++)
        {
            vec2 posNew = pos + cellSize * vec2(x, y);
            vec2 cellPos = mod(posNew, cellSize);
            vec2 cellCenter = posNew - cellPos;
            vec2 offset = rand(cellCenter, seed);
            vec2 cellPoint = cellCenter + offset;
            dist = min(dist, distance(pos, cellPoint));
        }
    }
    return dist;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 10.*(2.*fragCoord-iResolution.xy)/iResolution.y;
    vec3 col = vec3(voronoi(uv, 1.0));
    fragColor = vec4(col,1.0);
}
